mapper.update:
update wtm_member_score score set score.avaliableScore = score.memberScore +
            (select CASE m.totalScore is NULL WHEN TRUE THEN 0 ELSE m.totalScore END AS totalScore from (
            select SUM(f.flowScore) totalScore from wtm_member_score s LEFT JOIN
            wtm_member_score_flow f ON f.memberScoreId=s.id
            WHERE s.memberId=#{memberId} AND f.createTime < unix_timestamp(now()) AND f.createTime > #{time} AND f.typeId=7 AND f.flowScore < 0) m
            ) WHERE score.memberId=#{memberId}
